int Func(int n, int k)
{
    if (k == 1||n == k)
        return 1;
    else if (n < k)
        return 0;
    else
    {
        int sum = 0;
        n = n - k;
        while (k >= 1)
        {
            sum += Func(n,k); 
            k--;
        }
        return sum;
    }
